mapper.update:
UPDATE t_schedule_job SET FJOB_NAME=#{job.jobName}, FJOB_GROUP=#{job.jobGroup}, FJOB_STATUS=#{job.jobStatus},
FCRON_EXPRESSION=#{job.cronExpression},
FCONCURRENT=#{job.concurrent}, FDESC=#{job.desc}, FOPERATOR=#{job.operator}, FOP_TIME=#{job.opTime},FIDC=#{job.idc},FISBOOT=#{job.isBoot} WHERE FJOBID=#{job.jobId}

UPDATE t_schedule_job SET
FJOB_STATUS=#{job.jobStatus},
FOPERATOR=#{job.operator}, 
FOP_TIME=#{job.opTime} 
WHERE
FJOBID=#{job.jobId}

mapper.insert:
INSERT INTO t_schedule_job (
FJOB_NAME, FJOB_GROUP, FJOB_STATUS,
FCRON_EXPRESSION,FCONCURRENT,FDESC,
FOPERATOR, FOP_TIME,FIDC,FISBOOT
)
VALUES (
#{job.jobName},#{job.jobGroup},#{job.jobStatus},
#{job.cronExpression},#{job.concurrent},#{job.desc},
#{job.operator},#{job.opTime},#{job.idc},#{job.isBoot}
)

mapper.select:
SELECT COUNT(1) 
FROM 
t_schedule_job 
WHERE FJOB_NAME=#{job.jobName} AND FJOB_GROUP=#{job.jobGroup} AND FIDC=#{job.idc}
 AND FJOBID != #{job.jobId}
